import re

# Example string
text = 'the result of this is  dollar'
# text = 'the result is 13.0 and sum is 26.7'
# Use regex to find the first number from the last part of the text
# This regex finds all numbers and picks the last one in the string
numbers = re.findall(r'\d+\.\d+|\d+', text)
if numbers:
    last_number = numbers[-1]
    print(float(last_number))
else:
    print("No numbers found in the text.")
